class CatalogModel {
  static List<Item> items = [
    Item(
      id: 1,
      name: "Monkey D. Luffy",
      desc: "KING OF THE PIRATES",
      price: 999,
      color: "#ed1111",
      image:
          "data:image/jpeg;base64,",
    )
  ];
}

class Item {
  final int id;
  final String name;
  final String desc;
  final num price;
  final String color;
  final String image;

  Item(
      {required this.id,
      required this.name,
      required this.desc,
      required this.price,
      required this.color,
      required this.image});

  factory Item.fromMap(Map<String, dynamic> map) {
    return Item(
      id: map["id"],
      name: map["name"],
      desc: map["desc"],
      color: map["color"],
      image: map["image"],
      price: map["price"],
    );
  }
}
